import React, {Component} from 'react';

class Sam1 extends Component {
    constructor() {
      super();
      this.state = {
        message: "Hai I am Siva"
      };
    }
    changeMsg = () => {
        this.setState({
            message:  <script><div class="alert alert-success" role="alert">
              <h4 class="alert-heading">hai</h4>
              <p></p>
              <p class="mb-0"></p>
            </div></script>});
      }
    render() {
      return (
        <div>
          <h1> {this.state.message}</h1>
          <button type="button" onClick={this.changeMsg}>Click Me!</button>
        </div>
      );
    }
  }
export default Sam1;
